var App = angular.module('App', [
    'ngRoute',
    'Services',
    'Controllers'
]);



App.config(['$routeProvider',
    function($routeProvider){
        $routeProvider
            .when('/login',{
                templateUrl: 'html/views/login/loginView.php'
            })
            .when('/index',{
                templateUrl: 'html/views/index.php',
                controller: 'IndexController',
            })
            .when('/universities',{
                templateUrl: 'html/views/universities/universities.php',
                controller: 'UniversityController'
            })
            .otherwise({
                redirectTo: '/index'
            });
    }
]);