var Services=angular.module('Services', ['ngResource']);

Services.factory('AuthService', ['$resource',
    function ($resource) {
        return $resource('dispatcher.php', {}, {
            check: {url: 'checkAuth.do', method: 'POST'},
            login:{url: 'login.do', method: 'POST'},
            logout:{url: 'logout.do', method: 'POST'}
        });
    }
]);


Services.factory('RequestService', ['$resource',
    function ($resource) {
        return $resource('dispatcher.php', {}, {
            university: {url: 'university.do', method:'POST', isArray:true}
        });
    }
]);